package org.tnsindia.operator;

public class Bitwise_Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=8 ,b=2;
		int res1 = a & b;
		int res2 = a | b;
		int res3 = a ^ b;
		System.out.println(res1);
		System.out.println(res2);
		System.out.println(res3);


	}

}
