package org.tnsindia.array;

public class Array_Declaration {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 /* do not declare the size at the end if you are initializing the array
		 */
		int arr[] = new int[] {10,20,30,40};
		System.out.println("The array index value is = "+arr[0]);
		System.out.println("The array index value is = "+arr[1]);
		System.out.println("The array index value is = "+arr[2]);
		System.out.println("The array index value is = "+arr[3]);
		/*Array index out of bound exception as index is 4 we are accessing
		  and last index is 2
		System.out.println("The array index value is: "+arr[4]);*/
		
		
		 /* do not declare the size at the end if you are initializing the array
		 */
	}

}
